import './App.css';
import Formulario from './components/Formulario';
import Tarefa from './components/Tarefa';

const DOMAIN = 'localhost:9000';

function App() {
  const consultarTarefas = async () => {
    fetch(DOMAIN + '/consultar-tarefas')
    .then(response => {
      if (!response.ok) return null;
  
      response.blob();
    })
    .then(tarefasBanco => {
      tarefas = tarefasBanco;
    })
    .catch(error => {
      alert('Erro ao consultar suas tarefas: ' + error);
    });
  }

  let tarefas = [];
  consultarTarefas();
  alert(tarefas);

  return (
    <div className="card">
      <h1 className="titulo-app">Bem-vindo</h1> <br />

      <h3>Insira aqui a sua tarefa</h3>
      <Formulario />

      <h3>Suas tarefas</h3>
      <Tarefa />
    </div>
  );
}

export default App;
