import './App.css';
import Formulario from './components/Formulario';
import Tarefa from './components/Tarefa';

const DOMAIN = 'localhost:9000';

function App() {
  return (
    <div className="card">
      <h1 className="titulo-app">Bem-vindo</h1> <br />

      <h3>Insira aqui a sua tarefa</h3>
      <Formulario adicionarTarefa={adicionarTarefa}/>

      <h3>Suas tarefas</h3>
      <Tarefa />
    </div>
  );
}

const adicionarTarefa = () => {
    // Montando um objeto com os dados da tarefa
    let titulo = document.getElementById('titulo-tarefa').value;
    let data = document.getElementById('vencimento').value;
    let descricao = document.getElementById('descricao').value;
    let tarefa = {
        titulo: titulo,
        vencimento: data,
        completa: false,
        descricao: descricao,
        idUsuario: 1
    }
    
    // Configuracoes para o POST
    const options = {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json',
        },
        body: JSON.stringify(tarefa)
    }

    // Requisicao para a API
    fetch(DOMAIN + '/adicionar-tarefa', options).then((response) => {
      if (response.ok) {
        
      }
      else {
        alert("Resposta à requisição ");
      }
    }).catch(error => {
      alert("Erro ao adicionar tarefa: " + error);
    });
}

export default App;
