import './Carrossel.css';
import { useEffect, useState, useMemo } from 'react';
import Tarefa from './Tarefa';

export default function Carrossel() {
    const [ tarefas, setTarefas ] = useState(async () => {
        (await consultarTarefas()).map((tarefa) => {
            <Tarefa dados={tarefa} />
        });
    });

    useEffect(() => {
        setTarefas(async () => {
            (await consultarTarefas()).mna
        });
    }, [ tarefas ]);

    return (
        <div className="carrosel" id="carrossel"></div>
    );

    // Helper functions
    async function consultarTarefas() {
        let tarefas = [];
        
        fetch('http://localhost:9000/consultar-tarefas')
        .then((response) => {
            if (!response.ok) throw new Error('Falha na requisição');
        
            return response.json();
        })
        .then((data) => {
            tarefas = data;
        })
        .catch((error) => {
            alert('Erro ao consultar suas tarefas: ' + error);
        });
        
        return tarefas.map();
    }
}
