import { useState, useEffect } from 'react';
import './Tarefa.css';

export default function Tarefa(props) {
    
    // Hooks ----------------------------------------------------------------------------------------------------
    const [ id, setId ] = useState(props.dados['id']);

    useEffect(() => {
        document.querySelector('.descricao').value = props.dados['descricao'];
    }, [ id ]);

    return (
        <div className="tarefa" id={ id }>
            <div className='dados-tarefa'>
                {
                    /* Renderizacao do checkbox checked ou nao */
                    props.dados['completa'] === true ? <input type="checkbox" className="checkbox" checked/> :
                    <input type="checkbox" className="checkbox" />
                } &nbsp;
                <input type="text" className="titulo-tarefa" id={id + '-titulo-tarefa'} value={ props.dados['titulo'] }/> &nbsp;
                <input type="date" className="data" id={id + '-data'} value={ props.dados['vencimento'].split('T')[0] }/> &nbsp;
                <span className="material-icons">delete_forever</span> <br />
            </div>
            <textarea className="descricao" id={id + '-descricao'}>
                { props.dados['descricao'] }
            </textarea>
        </div>
    );
}
