import './Carrossel.css';
import { useEffect, useState } from 'react';
import Tarefa from './Tarefa';

export default function Carrossel() {
    let tarefas = [];

    // Ao renderizar, recarrega as tarefas do banco
    useEffect(async () => {
        tarefas = await consultarTarefas();
    });

    // Ao atualizar as tarefas, mapeia todas num componente Tarefa e insere um a um no carrossel
    // useEffect(() => {
    //     tarefas.map((tarefa) => <Tarefa dados={ tarefa }/>).forEach(tarefa => {
    //         document.getElementById('carrossel').appendChild(tarefa);
    //     });
    // }, [ tarefas ]);

    return (
        <div className="carrosel" id="carrossel"></div>
    );

    // Helper functions
    async function consultarTarefas() {
        let tarefas = [];
        
        fetch('http://localhost:9000/consultar-tarefas')
        .then((response) => {
            if (!response.ok) throw new Error('Falha na requisição');
        
            return response.json();
        })
        .then((data) => {
            tarefas = data;
        })
        .catch((error) => {
            alert('Erro ao consultar suas tarefas: ' + error);
        });
        
        return tarefas;
    }
}
